let optionObj = {
	method: 'post',
	headers: {
		'Content-Type': 'application/json' // application/x-www-form-urlencoded -> 기본값.
	},
	body: json
}
document.querySelector('#uploadBtn').onclick = function(e) {
	fetch(url, optionObj) // promise객체. then(), catch()
		.then(function(result) {
			console.log(result); //Response라고 하는 응답 객체.
			return result.json(); // json문자열 형태로 반환해주는 메소드.
		})
		.then(function(result) {
			console.log(result);
		})
		.catch(function(err) {
			console.log(err);
		})
}


//접종센터 정보.
let centerAry = [];
console.log('1', centerAry);


url = 'https://api.odcloud.kr/api/15077586/v1/centers?page=1&perPage=284&serviceKey=sCQafDrF26ZIzYiGuUZSBCV5FPkeqQ%2Fkq2qUsu7GkrBQRk1UwtwmJO%2BZdSxNymId%2B34PeESZMbVmGiHBo26vZw%3D%3D';
console.log('3', centerAry);





let arrayFunc = result => {
	
	document.querySelector('tbody').innerHTML='';
	console.log(result);
	centerAry = result.data; // [] 284건.
	console.log('2', centerAry);
	
	let randomCity = document.querySelector('#selectCenter').value;
	
	// forEach, map, filter, reduce.
	let newAry = centerAry.filter(center => center.sido == randomCity);
	console.log(newAry);
	let field = ['id', 'centerName', 'address', 'phoneNumber']
	newAry.forEach(center => {
		let tr = document.createElement('tr');
		tr.addEventListener('click', function(e) {
			//location.href = 'map.jsp?lat=' + center.lat + '&lng=' + center.lng; // 현재 위치 창
			window.open('map.jsp?lat=' + center.lat + '&lng=' + center.lng); // 공유창은 그대로 새창 열림 새창이당
		});
		for (let prop of field) {
			let td = document.createElement('td');
			td.innerHTML = center[prop];
			tr.append(td);
		}
		document.querySelector('tbody').append(tr);
	});
} // end of arrayFunc.

document.querySelector('#selectCenter').addEventListener('change', function(e) {
	fetch(url) // ajax.
	.then(result => result.json())
	.then(arrayFunc)
	.catch(err => console.error('에러=>', err));
})

